INSERT INTO `cms_master_template` (`id`, `template_name`, `template_content`, `is_active`, `created_date`, `created_by`, `updated_date`, `updated_by`) VALUES
(29, 'Document Archive Verify Approve', '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\">\r\n\r\n<html>\r\n<head>\r\n    <title>{APP_TITLE}</title>\r\n    <meta charset=\"utf-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width\">\r\n    <style type=\"text/css\">\r\n	/* CLIENT-SPECIFIC STYLES */\r\n    #outlook a{padding:0;} /* Force Outlook to provide a \"view in browser\" message */\r\n    .ReadMsgBody{width:100%;} .ExternalClass{width:100%;} /* Force Hotmail to display emails at full width */\r\n    .ExternalClass, .ExternalClass p, .ExternalClass span, .ExternalClass font, .ExternalClass td, .ExternalClass div {line-height: 100%;} /* Force Hotmail to display normal line spacing */\r\n    body, table, td, a{-webkit-text-size-adjust:100%; -ms-text-size-adjust:100%;} /* Prevent WebKit and Windows mobile changing default text sizes */\r\n    table, td{mso-table-lspace:0pt; mso-table-rspace:0pt;} /* Remove spacing between tables in Outlook 2007 and up */\r\n    img{-ms-interpolation-mode:bicubic;} /* Allow smoother rendering of resized image in Internet Explorer */\r\n    /* RESET STYLES */\r\n    body{margin:0; padding:0;}\r\n    img{border:0; height:auto; line-height:100%; outline:none; text-decoration:none;}\r\n    table{border-collapse:collapse !important;}\r\n    body{height:100% !important; margin:0; padding:0; width:100% !important;}\r\n    /* iOS BLUE LINKS */\r\n    .appleBody a {color:#68440a; text-decoration: none;}\r\n    .appleFooter a {color:#999999; text-decoration: none;}\r\n    /* MOBILE STYLES */\r\n    @media screen and (max-width: 480px) {\r\n                 .table_shrink {\r\n            width:95% !important;\r\n        }\r\n    }\r\n    </style>\r\n</head>\r\n\r\n<body>\r\n    <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" bgcolor=\"#FFFFFF\" class=\"table_shrink\" align=\"center\">\r\n        <tr>\r\n            <td>\r\n                <!-- start body module -->\r\n\r\n                <table width=\"520px\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" bgcolor=\"#FFFFFF\" class=\"table_shrink\" align=\"center\">\r\n                    <!-- start headline -->\r\n\r\n                    <tr>\r\n                        <td valign=\"top\" style=\"padding-top: 30px; font-family:Helvetica neue, Helvetica, Arial, Verdana, sans-serif; color: #205081; font-size: 20px; line-height: 32px; text-align:left; font-weight:bold;\" align=\"left\">\r\n						Notifications</td>\r\n                    </tr><!-- end headline -->\r\n\r\n                    <tr>\r\n                        <td style=\"color:#cccccc; padding-top:10px;\" valign=\"top\" width=\"100%\">\r\n                            <hr color=\"#CCCCCC\" size=\"1\">\r\n                        </td>\r\n                    </tr><!-- start headline -->\r\n                    <!-- end headline -->\r\n                    <!-- start copy -->\r\n\r\n                    <tr>\r\n                        <td valign=\"top\" style=\"padding-top: 20px; font-family: Helvetica, Helvetica neue, Arial, Verdana, sans-serif; color: #333333; font-size: 14px; line-height: 20px; text-align:left; font-weight:none;\" align=\"left\"> \r\n						Dear Sir,\r\n						</td>\r\n                    </tr>\r\n\r\n                    <tr>\r\n                        <td valign=\"top\" style=\"padding-top: 10px; font-family: Helvetica, Helvetica neue, Arial, Verdana, sans-serif; color: #333333; font-size: 14px; line-height: 20px; text-align:left; font-weight:none;\" align=\"left\">\r\n						{SENDER_NAME} has created document archiving as below. <br/>\r\n						Please verify and approve. <br/>\r\n                        </td>\r\n                    </tr>\r\n\r\n					<tr>\r\n                        <td valign=\"top\" align=\"left\" style=\"padding-top:20px; padding-bottom:20px;\">\r\n                            <table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\">\r\n                                <tr>\r\n                                    <td align=\"left\">\r\n										<b>Doc No.</b>\r\n									</td>\r\n									<td align=\"left\">\r\n										 : {DOC_NO}\r\n									</td>\r\n                                </tr>\r\n                                <tr>\r\n                                    <td align=\"left\">\r\n										<b>Doc Type</b>\r\n									</td>\r\n									<td align=\"left\">\r\n										: {TYPE_NAME}\r\n									</td>\r\n                                </tr>\r\n                            </table>\r\n                        </td>\r\n                    </tr>\r\n					\r\n                    <tr>\r\n                        <td valign=\"top\" style=\"padding-top: 0px; font-family: Helvetica, Helvetica neue, Arial, Verdana, sans-serif; color: #333333; font-size: 14px; line-height: 20px; text-align:left; font-weight:none;\" align=\"left\">\r\n						Regards,\r\n						<br/>\r\n						{MAIL_SIGNATURE}\r\n						</td>\r\n                    </tr>\r\n\r\n                    <!-- end copy -->\r\n                </table><!-- end body module -->\r\n            </td>\r\n        </tr>\r\n\r\n        <tr>\r\n            <td>\r\n                <!-- start footer module -->\r\n\r\n                <table width=\"520px\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" bgcolor=\"#FFFFFF\" class=\"table_shrink\" align=\"center\">\r\n                    <tr>\r\n                        <td style=\"color:#cccccc;\" valign=\"top\" width=\"100%\">\r\n                            <hr color=\"#CCCCCC\" size=\"1\">\r\n                        </td>\r\n                    </tr>\r\n\r\n                    <tr>\r\n                        <td>\r\n                            <table width=\"520px\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" bgcolor=\"#FFFFFF\" class=\"table_shrink\" align=\"center\">\r\n                                <tr>\r\n                                    <td valign=\"top\" style=\"padding-top: 10px; font-family: Helvetica, Helvetica neue, Arial, Verdana, sans-serif; color: #707070; font-size: 12px; line-height: 18px; text-align:left; font-weight:none;\" align=\"left\">\r\n									\r\n                                    This message was sent to you by {APP_TITLE}<br></td>\r\n\r\n                                    <td align=\"right\" style=\"padding-top:15px; padding-bottom:30px;\">\r\n									\r\n									</td>\r\n                                </tr>\r\n                            </table>\r\n                        </td>\r\n                    </tr>\r\n                </table><!-- end footer module -->\r\n            </td>\r\n        </tr>\r\n    </table>\r\n</body>\r\n</html>', 1, NULL, NULL, NULL, NULL),
